mapper.select:

select bno, productName, productPrice, ProductDesc, productImg, productThumbImg, productCount, productKate
from
(
select /*+INDEX_DESC(tbl_products pk_products) */
rownum rn, bno, productName, productPrice, ProductDesc, productImg, productThumbImg, productCount, productKate
from 
tbl_products 
where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1) * #{amount}
